tinymce.PluginManager.add('uploadrecipe', function (editor) {

    function uploadrecipe() {   
        $("#srecipe_loading").fadeIn();
        
        $("#searchrecipe iframe").attr("src","../zt_add/zt_recipe_search.php");
        $("#searchrecipe").show();


    }

    editor.addCommand("mceuploadrecipe", uploadrecipe);

    editor.addButton('uploadrecipe', {
        icon: 'image',
        tooltip: '插入菜谱',
        image:"http://s1.c.meishij.net/zt_new1/tinymce/plugins/uploadrecipe/img/book.png",
        onclick: uploadrecipe
    });

    editor.addMenuItem('uploadrecipe', {
        icon: 'image',
        text: '插入菜谱',
        image:"http://s1.c.meishij.net/zt_new1/tinymce/plugins/uploadrecipe/img/book.png",
        context: 'tools',
        onclick: uploadrecipe
    });
});
